                <input type="text" name="" id="" onChange={handleChange} placeholder='Title' value={note.title} />
                <textarea name="" id="" rows="3" onChange = {handleChange} placeholder="Take a note...." value={note.content}></textarea>
                <button>Add</button>
            </form>
        </div>
    )
}
