import React from 'react'

export default function Note(props) {

  return (
    <div id = {props.id} className = "notes">
        <h1>{props.title}</h1>
        <button onClick = {() => {props.delNote(props.id)}}><ion-icon name="trash-outline"></ion-icon></button>
        <p>{props.content}</p>
    </div>
  )
}
