import React from 'react'
import { useState } from 'react'

export default function CreateArea(props) {
    const [note, setNote] = useState({
        title: '',
        content: ''
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNote((prev) => {
            return {
                ...prev,
                [name]: value
            }
        })
    }

    const handleClick = (e) => {
        props.onAdd(note);
        setNote({
            title: "",
            content: ""
        })
        e.preventDefault();
    }

    return (
        <div>
            <form>
                <input type="text" name="title" id="" onChange={handleChange} placeholder='Title' value={note.title} />
                <textarea name="content" id="" rows="3" onChange={handleChange} placeholder="Take a note...." value={note.content}></textarea>
                <button onClick={handleClick}><ion-icon name="add-outline"></ion-icon></button>
            </form>
        </div>
    )
}
