import React, {useState} from 'react'

export default function CreateArea(props) {
    return (
        <div>
            <form>
                <input type="text" name="" id="" placeholder='Title' onChange = {props.onChangeTitle}/>
                <textarea name="" id="" rows="3" placeholder="Take a note...." onChange = {props.OnChangeContent}></textarea>
                <button onClick = {props.handleClick}>Add</button>
            </form>
        </div>
    )
}
